/*Table structure for table `news_column` */

DROP TABLE IF EXISTS `news_column`;

CREATE TABLE `news_column` (
  `id` varchar(32) NOT NULL COMMENT '主键：id',
  `name` varchar(20) DEFAULT NULL COMMENT '名称',
  `level` int(11) DEFAULT NULL COMMENT '等级',
  `parent_id` varchar(32) DEFAULT NULL COMMENT '兑奖地址',
  `create_user_id` varchar(32) DEFAULT NULL COMMENT '创建人id',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_user_id` varchar(32) DEFAULT NULL COMMENT '修改人id',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `type` int(11) DEFAULT NULL COMMENT '类型( 1.文章类别 2.文章 3.链接 )',
  `content` text COMMENT '内容',
  `link` varchar(100) DEFAULT NULL COMMENT '链接',
  `sort_index` int(11) DEFAULT '0' COMMENT '排序权重',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='新闻栏目表';

