insert  into `news_column`(`id`,`name`,`level`,`parent_id`,`create_user_id`,`create_time`,`update_user_id`,`update_time`,`type`,`content`,`link`,`sort_index`) values ('D4E30900A4B24749BE25075601A4BEAF','科研成果',1,NULL,'0001','2019-01-11 22:26:02','0001','2019-01-19 19:57:52',1,'<p><span><span><span>一、图像处理</span></span></span></p><p><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 研究方向：</span>以应用基础研究为重点，致力于图像分割、图像增强、图像去噪、图像识别等图像处理技术的研究和开发。</span></p><p><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 科研成果： 拟开展数字医疗、智能交通等方面的应用研究和技术开发。</span></p><p><span><span><span>二、文本语义分析</span></span></span></p><p><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 研究方向：以语料资源自动构建和语义相似度计算为主要研究对象。</span></p><p><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 科研成果：拟开展自动问答系统、网络信息情感分析的应用研究。</span></p><p><span><span><span>三、工业智能控制</span></span></span></p><p><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 研究方向：</span>以机器视觉技术在工业生产中的应用为主要研究对象。</span></p><p><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 科研成果:拟结合机器视觉系统，建设一套具有机器视觉功能的全自动化机器人生产线，涵盖上料、传输、加工、检测、包装、搬运等自动化生产线基本功能。</span></p>',NULL,8);
