mapper.select:
select * from active
where 1=1

  and id = #{id}


  and title = #{title}


  and create_user_id = #{createUserId}


  and create_time = #{createTime}


  and update_user_id = #{updateUserId}


  and update_time = #{updateTime}


  and pic_url = #{picUrl}


  and status = #{status}


  and content = #{content}

mapper.delete:
delete from active
where id in(

  #{item}

)

mapper.select:
select 
	*,
	(select count(*) from active_user where active_id = active.id) joinCount  
from active
where 1=1

  and id = #{id}


  and title like concat('%',#{title},'%')


  and create_user_id = #{createUserId}


  and create_time = #{createTime}


  and update_user_id = #{updateUserId}


  and update_time = #{updateTime}


  and pic_url = #{picUrl}


  and status = #{status}


  and content = #{content}

select count(*) from active
where 1=1

  and id = #{id}


  and title = #{title}


  and create_user_id = #{createUserId}


  and create_time = #{createTime}


  and update_user_id = #{updateUserId}


  and update_time = #{updateTime}


  and pic_url = #{picUrl}


  and status = #{status}


  and content = #{content}
