mapper.select:
select * from resume
where 1=1

  and id = #{id}


  and title = #{title}


  and create_user_id = #{createUserId}


  and create_time = #{createTime}


  and update_user_id = #{updateUserId}


  and update_time = #{updateTime}


  and status = #{status}


  and recruitment_id = #{recruitmentId}


  and user_realname = #{userRealname}


  and phone = #{phone}


  and student_code = #{studentCode}


  and content = #{content}

